//SPDX-License-Identifier: LGPL-3.0-only
pragma solidity 0.8.11;

import "../interfaces/IVote.sol";

/**
 * @notice This is a simple mock exposing the Manager-facing Vote API as
 * simple functions that
 * 1. Return currently locked stCELO.
 */
contract MockVote is IVote {
    address public accountVoter;
    uint256 public proposalId;
    uint256 public totalYesVotes;
    uint256 public totalNoVotes;
    uint256 public totalAbstainVotes;

    address public revokeAccountVoter;
    uint256 public revokeProposalId;

    address public updatedHistoryFor;

    function updateHistoryAndReturnLockedStCeloInVoting(address beneficiary)
        external
        override
        returns (uint256)
    {
        updatedHistoryFor = beneficiary;
        return 111;
    }

    function voteProposal(
        address _accountVoter,
        uint256 _proposalId,
        uint256 _yesVotes,
        uint256 _noVotes,
        uint256 _abstainVotes
    )
        external
        override
        returns (
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        accountVoter = _accountVoter;
        proposalId = _proposalId;
        totalYesVotes = _yesVotes;
        totalNoVotes = _noVotes;
        totalAbstainVotes = _abstainVotes;

        return (
            _yesVotes + _noVotes + _abstainVotes,
            totalYesVotes,
            totalNoVotes,
            totalAbstainVotes
        );
    }

    function revokeVotes(address _accountVoter, uint256 _proposalId)
        external
        override
        returns (
            uint256,
            uint256,
            uint256
        )
    {
        revokeAccountVoter = _accountVoter;
        revokeProposalId = _proposalId;
        return (totalYesVotes, totalNoVotes, totalAbstainVotes);
    }

    function setVotes(
        uint256 yes,
        uint256 no,
        uint256 abstain
    ) public {
        totalYesVotes = yes;
        totalNoVotes = no;
        totalAbstainVotes = abstain;
    }
}

contracts/RebasedStakedCelo.sol.content:
//SPDX-License-Identifier: LGPL-3.0-only
pragma solidity 0.8.11;

import "./common/ERC20Upgradeable.sol";
import "./common/UUPSOwnableUpgradeable.sol";
import "./Managed.sol";
import "./interfaces/IAccount.sol";
import "./interfaces/IStakedCelo.sol";
import "./Pausable.sol";
import "./common/Errors.sol";

/**
 * @title An ERC-20 wrapper contract that receives stCELO
 * and represents the underlying voted LockedGold in the StakedCelo system.
 * @dev This contract depends on the Account and StakedCelo contracts
 * to calculate the amount of rstCELO held by depositors.
 */
contract RebasedStakedCelo is Errors, ERC20Upgradeable, UUPSOwnableUpgradeable, Pausable {
    /**
     * @notice Total amount of stCELO deposited in this contract.
     */
    uint256 public totalDeposit;

    /**
     * @notice Keyed by depositor address, the amount of stCELO deposited.
     */
    mapping(address => uint256) public stakedCeloBalance;

    /**
     * @notice An instance of the StakedCelo contract.
     */
    IStakedCelo internal stakedCelo;

    /**
     * @notice An instance of the Account contract.
     */
    IAccount internal account;

    /**
     * @notice Used when a deposit is successfuly completed.
     * @param depositor The address of the depositor.
     * @param amount The amount of stCELO deposited.
     */
    event StakedCeloDeposited(address indexed depositor, uint256 amount);

    /**
     * @notice Used when a withdrawal is successfully completed.
     * @param withdrawer The address of the withdrawer.
     * @param amount The amount of stCELO withdrawn.
     */
    event StakedCeloWithdrawn(address indexed withdrawer, uint256 amount);

    /**
     * @notice Used when the deposit amount is zero.
     */
    error ZeroAmount();

    /**
     * @notice Used when a balance is too low.
     * @param amount The amount of stCELO that is insufficient.
     */
    error InsufficientBalance(uint256 amount);

    /**
     * @notice Used when deposit fails.
     * @param depositor The address of the depositor.
     * @param amount The amount of stCELO the depositor attempted to deposit.
     */
    error FailedDeposit(address depositor, uint256 amount);

    /**
     * @notice Used when withdrawal fails.
     * @param withdrawer The address of the withdrawer.
     * @param amount The amount of stCELO the withdrawer attempted to withdraw.
     */
    error FailedWithdrawal(address withdrawer, uint256 amount);

    /**
     * Used when input amount of token is greater than total token amount.
     */
    error InputLargerThanTotalAmount();

    /**
     * @notice Empty constructor for proxy implementation, `initializer` modifer ensures the
     * implementation gets initialized.
     */
    // solhint-disable-next-line no-empty-blocks
    constructor() initializer {}

    /**
     * @notice Replaces the constructor for proxy implementation.
     * @param _stakedCelo The address of the StakedCelo contract.
     * @param _account The address of the Account contract.
     * @param _owner The address of the contract owner.
     */
    function initialize(
        address _stakedCelo,
        address _account,
        address _owner
    ) external initializer {
        __ERC20_init("Rebased Staked CELO", "rstCELO");
        _transferOwnership(_owner);
        stakedCelo = IStakedCelo(_stakedCelo);
        account = IAccount(_account);
    }

    /**
     * @notice Sets that address permissioned to pause/unpause this contract to
     * the owner of this contract.
     */
    function setPauser() external onlyOwner {
        _setPauser(owner());
    }

    /**
     * @notice Deposit stCELO in return for rstCELO.
     * @dev Although rstCELO is never minted to any account, the rstCELO balance
     * is calculated based on the account's deposited stCELO. See `balanceOf()` function below.
     * @param stCeloAmount The Amount of stCELO to be deposited.
     */
    function deposit(uint256 stCeloAmount) external onlyWhenNotPaused {
        if (stCeloAmount == 0) {
            revert ZeroAmount();
        }

        totalDeposit += stCeloAmount;

        stakedCeloBalance[msg.sender] += stCeloAmount;

        emit StakedCeloDeposited(msg.sender, stCeloAmount);

        if (!stakedCelo.transferFrom(msg.sender, address(this), stCeloAmount)) {
            revert FailedDeposit(msg.sender, stCeloAmount);
        }
    }

    /**
     * @notice Withdraws stCELO. This function transfers back some or all of sender's
     * previously deposited stCELO amount.
     * @param stCeloAmount The amount of stCELO to withdraw.
     */
    function withdraw(uint256 stCeloAmount) external onlyWhenNotPaused {
        if (stCeloAmount > stakedCeloBalance[msg.sender]) {
            revert InsufficientBalance(stCeloAmount);
        }

        totalDeposit -= stCeloAmount;

        unchecked {
            stakedCeloBalance[msg.sender] -= stCeloAmount;
        }
        emit StakedCeloWithdrawn(msg.sender, stCeloAmount);

        if (!stakedCelo.transfer(msg.sender, stCeloAmount)) {
            revert FailedWithdrawal(msg.sender, stCeloAmount);
        }
    }

    /**
     * @notice Returns the storage, major, minor, and patch version of the contract.
     * @return Storage version of the contract.
     * @return Major version of the contract.
     * @return Minor version of the contract.
     * @return Patch version of the contract.
     */
    function getVersionNumber()
        external
        pure
        returns (
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        return (1, 1, 2, 0);
    }

    /**
     * @notice Used to query the total supply of rstCELO.
     * @return The calculated total supply of rstCELO.
     */
    function totalSupply() public view virtual override returns (uint256) {
        return toRebasedStakedCelo(totalDeposit);
    }

    /**
     * @notice Used to query the rstCELO balance of an address.
     * @param _account The address of interest.
     * @return The amount of rstCELO owned by the address.
     */
    function balanceOf(address _account) public view override returns (uint256) {
        return toRebasedStakedCelo(stakedCeloBalance[_account]);
    }

    /**
     * @notice Computes the amount of stCELO that is represented by an amount of rstCELO.
     * @param rstCeloAmount The amount of rstCELO.
     * @return The amount of stCELO represented by rstCELO.
     */
    function toStakedCelo(uint256 rstCeloAmount) public view returns (uint256) {
        uint256 stCeloSupply = stakedCelo.totalSupply();
        uint256 celoBalance = account.getTotalCelo();

        uint256 rstSupply = totalSupply();
        if (rstSupply < rstCeloAmount) {
            revert InputLargerThanTotalAmount();
        }

        if (stCeloSupply == 0 || celoBalance == 0) {
            return rstCeloAmount;
        }

        return (rstCeloAmount * stCeloSupply) / celoBalance;
    }

    /**
     * @notice Computes the amount of rstCELO that is represented by an amount of stCELO.
     * @param stCeloAmount The amount of stCELO.
     * @return The amount of rstCELO represented by stCELO.
     */
    function toRebasedStakedCelo(uint256 stCeloAmount) public view returns (uint256) {
        uint256 stCeloSupply = stakedCelo.totalSupply();
        uint256 celoBalance = account.getTotalCelo();

        if (stCeloSupply < stCeloAmount) {
            revert InputLargerThanTotalAmount();
        }

        if (stCeloSupply == 0 || celoBalance == 0) {
            return stCeloAmount;
        }

        return (stCeloAmount * celoBalance) / stCeloSupply;
    }

    /**
     * @notice Moves `amount` of rstCELO from `sender` to `recipient`.
     * @param from The address of the sender.
     * @param to The address of the receiver.
     * @param amount The amount of rstCELO to transfer.
     */
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override onlyWhenNotPaused {
        if (from == address(0)) {
            revert AddressZeroNotAllowed();
        }
        if (to == address(0)) {
            revert AddressZeroNotAllowed();
        }

        uint256 fromBalance = stakedCeloBalance[from];
        uint256 equivalentStakedCeloAmount = toStakedCelo(amount);
        if (fromBalance < equivalentStakedCeloAmount) {
            revert InsufficientBalance(amount);
        }

        unchecked {
            stakedCeloBalance[from] = fromBalance - equivalentStakedCeloAmount;
        }
        stakedCeloBalance[to] += equivalentStakedCeloAmount;

        emit Transfer(from, to, amount);
    }

    function _approve(
        address from,
        address to,
        uint256 amount
    ) internal override onlyWhenNotPaused {
        super._approve(from, to, amount);
    }
}

contracts/GroupHealth.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity 0.8.11;

import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import "./common/UsingRegistryUpgradeable.sol";
import "./common/UUPSOwnableUpgradeable.sol";
import "./Pausable.sol";

/**
 * @title GroupHealth stores and updates info about validator group health.
 */
contract GroupHealth is UUPSOwnableUpgradeable, UsingRegistryUpgradeable, Pausable {
    using EnumerableSet for EnumerableSet.AddressSet;

    /**
     * @notice Mapping that stores health state of groups.
     */
    mapping(address => bool) public isGroupValid;

    /**
     * @notice Used as helper varible during call to `areGroupMembersElected`.
     */
    mapping(address => bool) private membersMappingHelper;

    /**
     * @notice Emitted when `updateGroupHealth` called.
     * @param group The group's address.
     * @param healthy Whether or not group is healthy.
     */
    event GroupHealthUpdated(address group, bool healthy);

    /**
     * @notice Used when checking elected validator group members
     * but there is member length and indexes length mismatch.
     */
    error MembersLengthMismatch();

    /**
     * @notice Used when calling `markGroupHealthy` on already healthy group.
     * @param group The group's address.
     */
    error GroupHealthy(address group);

    /**
     * @notice Empty constructor for proxy implementation, `initializer` modifer ensures the
     * implementation gets initialized.
     */
    // solhint-disable-next-line no-empty-blocks
    constructor() initializer {}

    /**
     * @notice Initialize the contract with registry and owner.
     * @param _registry The address of the CELO Registry.
     * @param _owner The address of the contract owner.
     */
    function initialize(address _registry, address _owner) external initializer {
        _transferOwnership(_owner);
        __UsingRegistry_init(_registry);
    }

    /**
     * @notice Sets that address permissioned to pause/unpause this contract to
     * the owner of this contract.
     */
    function setPauser() external onlyOwner {
        _setPauser(owner());
    }

    /**
     * @notice Returns the storage, major, minor, and patch version of the contract.
     * @return Storage version of the contract.
     * @return Major version of the contract.
     * @return Minor version of the contract.
     * @return Patch version of the contract.
     */
    function getVersionNumber()
        external
        pure
        returns (
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        return (1, 1, 1, 0);
    }

    /**
     * @notice Updates validator group health.
     * @param group The group address.
     */
    function updateGroupHealth(address group) public onlyWhenNotPaused {
        IValidators validators = getValidators();

        (bool valid, address[] memory members) = _isGroupPartiallyValid(validators, group);
        if (valid) {
            valid = areGroupMembersElected(members);
        }

        if (isGroupValid[group] != valid) {
            isGroupValid[group] = valid;
            emit GroupHealthUpdated(group, valid);
        }
    }

    /**
     * @notice Updates validator group to healthy if eligible.
     * @param group The group's address.
     * @param membersElectedIndex The indexes of elected members.
     * This array needs to have same length as all (even not elected) members of validator group.
     * Index of not elected member can be any uint256 number.
     */
    function markGroupHealthy(address group, uint256[] calldata membersElectedIndex)
        public
        onlyWhenNotPaused
    {
        if (isGroupValid[group] == true) {
            revert GroupHealthy(group);
        }

        IValidators validators = getValidators();

        (bool valid, address[] memory members) = _isGroupPartiallyValid(validators, group);

        if (!valid) {
            return;
        }

        if (membersElectedIndex.length != members.length) {
            revert MembersLengthMismatch();
        }

        uint256 currentNumberOfElectedValidators = numberValidatorsInCurrentSet();
        // check that at least one member is elected.
        for (uint256 i = 0; i < members.length; i++) {
            if (
                isGroupMemberElected(
                    members[i],
                    membersElectedIndex[i],
                    currentNumberOfElectedValidators
                )
            ) {
                isGroupValid[group] = true;
                emit GroupHealthUpdated(group, true);
                return;
            }
        }
    }

    /**
     * @notice Gets a validator address from the current validator set.
     * @param index Index of requested validator in the validator set.
     * @return Address of validator at the requested index.
     */
    function validatorSignerAddressFromCurrentSet(uint256 index)
        internal
        view
        virtual
        returns (address)
    {
        return getElection().validatorSignerAddressFromCurrentSet(index);
    }

    /**
     * @notice Gets the size of the current elected validator set.
     * @return Size of the current elected validator set.
     */
    function numberValidatorsInCurrentSet() internal view virtual returns (uint256) {
        return getElection().numberValidatorsInCurrentSet();
    }

    /**
     * @notice Checks if a group member is elected.
     * @param groupMember The member of the group to check election status for.
     * @param index The index of elected validator in current set.
     * @param currentNumberOfElectedValidators The count of currently elected validators.
     * @return Whether or not the group member is elected.
     */
    function isGroupMemberElected(
        address groupMember,
        uint256 index,
        uint256 currentNumberOfElectedValidators
    ) internal view returns (bool) {
        if (index > currentNumberOfElectedValidators) {
            return false;
        }
        return validatorSignerAddressFromCurrentSet(index) == groupMember;
    }

    /**
     * @notice Checks if any of group members are elected.
     * @param members All group members of checked validator group.
     * @return Whether or not any of the group members are elected.
     */
    function areGroupMembersElected(address[] memory members) private returns (bool) {
        for (uint256 j = 0; j < members.length; j++) {
            membersMappingHelper[members[j]] = true;
        }

        bool result;
        address validator;
        uint256 n = numberValidatorsInCurrentSet();
        for (uint256 i = 0; i < n; i++) {
            validator = validatorSignerAddressFromCurrentSet(i);
            if (membersMappingHelper[validator] == true) {
                result = true;
                break;
            }
        }

        for (uint256 j = 0; j < members.length; j++) {
            delete membersMappingHelper[members[j]];
        }
        return result;
    }

    /**
     * Checks group validator status, members and slashing multiplier.
     * @param validators Validators contract.
     * @param group The group to check.
     * @return Whether the group passed checks.
     * @return members The members of the validator group.
     */
    function _isGroupPartiallyValid(IValidators validators, address group)
        private
        view
        returns (bool, address[] memory members)
    {
        if (!validators.isValidatorGroup(group)) {
            return (false, members);
        }

        uint256 slashMultiplier;
        address[] memory originalMembers;
        (originalMembers, , , , , slashMultiplier, ) = validators.getValidatorGroup(group);

        members = new address[](originalMembers.length);

        IAccounts accounts = getAccounts();

        for (uint256 i = 0; i < originalMembers.length; i++) {
            members[i] = accounts.getValidatorSigner(originalMembers[i]);
        }

        // check if group has no members
        if (members.length == 0) {
            return (false, members);
        }
        // check for recent slash
        if (slashMultiplier < 10**24) {
            return (false, members);
        }

        return (true, members);
    }
}

